//  makeNumbers.cpp/*  This program will generate a file of integers.  The user will provide the number of values	to generate and the largest integer to create.  You must add the file location and name	to save as.  */	#include <iostream.h>#include <fstream.h>#include "dice.h"		// from Lesson 12main(){	int  howMany, largest, loop;	ofstream outfile;		cout << "How many values do you wish to generate ---> ";	cin >> howMany;	cout << "What is the largest integer you wish to create ---> ";	cin >> largest;		dice temp(largest);		// initialized using largest		outfile.open("Macintosh HD:num15.txt");	for (loop=1; loop<=howMany; loop++)		outfile << temp.roll() << endl;}