// math.cpp// solve some simple math problems// Cary Matsuoka#include <iostream.h>#include <limits.h>#include <float.h>void main (){	int  a;	float  x;	char c;	a = 4 + 9;	cout << "4 + 9 = " << a << endl;	a = 46 / 7;	cout << "46 / 7 = " << a << endl;	a = 46 % 7;	cout << "46 % 7 = " << a << endl;	x = 2 * 3.0;	cout << "2 * 3.0 = " << x << endl;	x = float(25)/4;	cout << "float(25)/4 = " << x << endl;	a = int(7.75) + 2;	cout << "int(7.75) + 2 = " << a << endl;	a = int('P');	cout << "int('P') = " << a << endl;	c = char(105);	cout << "char(105) = " << c << endl << endl;	cout << "Largest 2-byte integer = " << INT_MAX << endl;	cout << "Smallest negative 2-byte integer = " << INT_MIN << endl;	cout << "Largest unsigned 2-byte integer = " << UINT_MAX << endl;	cout << "Largest 4-byte integer = " << LONG_MAX << endl;	cout << "Smallest negative 4-byte integer = " << LONG_MIN << endl;	cout << "Largest unsigned 4-byte integer = " << ULONG_MAX << endl;	cout << endl;	cout << "Largest float = " << FLT_MAX << endl;	cout << "Smallest negative float = " << FLT_MIN << endl;	cout << "Largest double float = " << DBL_MAX << endl;	cout << "Smallest negative double float = " << DBL_MIN << endl;}