// Practice.cpp#include <iostream.h>#include <bool.h>main (){	int		number = 125;	long int	bignumber = 1234567;	unsigned int	posnumber = 75;	char		letter = 'p';	float		realnum = 7.7505;	double	doublereal = 123456.7890000;	long double	bigreal = 6.023e23;	int		size, position;	bool  done = true;	size = sizeof (number);	cout << "number = " << number << "    Size of an integer = " << size		<< endl;	size = sizeof (bignumber);	cout << "bignumber = " << bignumber << "   Size of a long integer = "		<< size << endl;	size = sizeof (posnumber);	cout << "posnumber = " << posnumber << "   Size of an unsigned integer = "		<< size << endl;	size = sizeof (letter);	cout << "letter = " << letter << "   Size of a character = " << size		<< endl;	size = sizeof (realnum);	cout << "realnum = " << realnum << "   Size of a float = " << size		<< endl;	size = sizeof (doublereal);	cout << "doublereal = " << doublereal << "   Size of a double float = "		<< size << endl;	size = sizeof (bigreal);	cout << "bigreal = " << bigreal << "   Size of a long double = "		<< size << endl;	size = sizeof (done);	cout << "done = " << done << "   Size of a bool = " << size << endl;		position = int (letter);	cout << "Letter = " << letter << "     ASCII position = " << position << endl;		position = 60;	letter = char(position);	cout << "ASCII position = " << position << "     Letter = " << letter << endl;	return 0;}