// carrent.cpp#include <iostream.h>#include <apstring.h>main (){	char c1,c2,c3;	//  to hold the first three letters	int  num;		//  to hold the three digit integer	int  total, position;	char letter;	apstring carMake, carModel;		cout << "Enter make of car ---> ";	cin >> carMake;	cout << "Enter model of car ---> ";	cin >> carModel;	cout << endl;	cout << "Enter three letters then three digits (ABC 123) ---> ";	cin >> c1 >> c2 >> c3 >> num;		total = c1 + c2 + c3 + num;	position = total % 26;	letter = char (65 + position);	cout << endl << "make - " << carMake << endl;	cout << "model - "<< carModel << endl;	cout << c1 << c2 << c3 << " " << num << "   --->  " << letter << total;	return 0;}