// taxes.cpp#include <iostream.h>#include <iomanip.h>main (){	const  double		FEDTAXRATE = 0.15,		FICATAXRATE = 0.08,		STATETAXRATE = 0.032;	cout << endl << "Enter number of hours worked ---> ";	int Hours;	cin >> Hours;	cout << endl << "Enter hourly rate ---> ";	double Rate;	cin >> Rate;	double  GrossPay = Hours * Rate;	double  FedTax = GrossPay * FEDTAXRATE;	double  FicaTax = GrossPay * FICATAXRATE;	double  StateTax = GrossPay * STATETAXRATE;	double  NetPay = GrossPay - FedTax - FicaTax - StateTax;	cout << endl << endl;	cout << setw (20) << setiosflags (ios::left) << "Hours worked:" <<	setw(8) << setiosflags (ios::right) << Hours << endl;	cout << setw (20) << setiosflags (ios::left) << "Hourly rate:" <<	setw(8) << setiosflags (ios::right) << Rate << endl << endl;	cout << setw (20) << setiosflags (ios::left) << "Gross pay" <<	setw(8) << setiosflags (ios::right | ios::fixed | ios::showpoint)	<< setprecision(2) << GrossPay << endl << endl;	cout << setw (20) << setiosflags (ios::left) << "Federal tax" <<	setw(8) << setiosflags (ios::right | ios::fixed | ios::showpoint)	<< setprecision(2) << FedTax << endl;	cout << setw (20) << setiosflags (ios::left) << "FICA" <<	setw(8) << setiosflags (ios::right | ios::fixed | ios::showpoint)	<< setprecision(2) << FicaTax << endl;	cout << setw (20) << setiosflags (ios::left) << "State tax" <<	setw(8) << setiosflags (ios::right | ios::fixed | ios::showpoint)	<< setprecision(2) << StateTax << endl << endl;	cout << setw (20) << setiosflags (ios::left) << "Net pay" <<	setw(8) << setiosflags (ios::right | ios::fixed | ios::showpoint)	<< setprecision(2) << NetPay;	return 0;}