// Quadratic.cpp#include <math.h>#include <iostream.h>#include <iomanip.h>main (){	double   A, B, C, Determinate, Root1, Root2;	cout << endl << "Quadratic equation formula" << endl << endl;	cout << "Enter coefficient a ---> ";	cin >> A;	cout << "Enter coefficient b ---> ";	cin >> B;	cout << "Enter coefficient c ---> ";	cin >> C;	Determinate = sqrt(pow(B,2) - 4*A*C);	Root1 = (-B + Determinate)/2*A;	Root2 = (-B - Determinate)/2*A;	cout << endl << endl;	cout << setprecision(4) << setiosflags (ios::showpoint);	cout << "Root 1 = " << Root1;	cout << "       Root 2 = " << Root2;	return 0;}