// fun.cpp// Lesson 06#include <iostream.h>#include <math.h>#include <iomanip.h>double fToC (double farenheit);double cToF (double celsius);double sphereVol (double radius);double findHypo (double sideA, double sideB);main(){	cout << setiosflags (ios::fixed) << setprecision(2);	cout << "Temperature conversions" << endl << endl;	cout << "212 F ---> " << fToC(212) << endl;	cout << "98.6 F ---> " << fToC(98.6) << endl;	cout << "10 F ---> " << fToC(10) << endl << endl;		cout << "-15 C ---> " << cToF(-15) << endl;	cout << "0 C ---> " << cToF(0) << endl;	cout << "70 C ---> " << cToF(70) << endl << endl;	 	cout << "Volume of sphere (r=1.0) = " << sphereVol(1.0) << endl;	cout << "Volume of sphere (r=2.25) = " << sphereVol(2.25) << endl;	cout << "Volume of sphere (r=7.50) = " << sphereVol(7.50) << endl << endl;		cout << "Hypotenuse (sides 3.0, 4.0) = " << findHypo(3.0,4.0) << endl;	cout << "Hypotenuse (sides 6.75, 12.31) = " << findHypo(6.75,12.31) << endl;	return 0;}double fToC (double farenheit){	return 5.0/9.0 * (farenheit - 32);}double cToF (double celsius){	return (9.0/5.0 * celsius) + 32;}double sphereVol (double r){	return 4.0/3.0 * 3.1415927 * r * r * r;}double findHypo (double sideA, double sideB){	return sqrt((sideA * sideA) + (sideB * sideB));}