#include <iostream.h>int a = 12;  // global variable// function prototypesvoid one (int number);void two (int value);void three ();main (){	int  s = 5;	one (s);	cout << s << endl;	two (a);	cout << a << endl;	three ();	cout << a << endl;	return 0;}void one (int number){	number *= 3;	cout << number << endl;}void two (int value){	value /= 5;	cout << value << endl;	a *= 3;	{		int  a = 7;		a += 3;		cout << a << endl;	}	cout << a << endl;}void three (){	int a = 8;	a += 9;	cout << a << endl;	cout << ::a << endl;}