// fun2.cpp#include <iostream.h>void get2Values (int&, int&);void get2Values (float&, float&);void print2Values (const int &, const int &);void print2Values (const float &, const float &);template <class dataType>void swap (dataType &, dataType &);main (){	int  a,b;	float  x,y;	get2Values (a,b);	get2Values (x,y);	cout << endl << "The two integers:    ";	print2Values (a,b);	cout << endl << "The two floats:    ";	print2Values (x,y);	swap (a,b);	swap (x,y);	cout << endl << "The two integers after swap:    ";	print2Values (a,b);	cout << endl << "The two floats after swap:    ";	print2Values (x,y);	return 0;}void get2Values (int &one, int &two){	cout << "Enter your first integer ---> ";	cin >> one;	cout << "Enter your second integer ---> ";	cin >> two;	cout << endl;}void get2Values (float &first, float &second){	cout << "Enter your first float ---> ";	cin >> first;	cout << "Enter your second float ---> ";	cin >> second;	cout << endl;}void print2Values (const int &one, const int &two){	cout << one << "   " << two << endl;}void print2Values (const float &first, const float &second){	cout << first << "   " << second << endl;}template <class dataType>void swap (dataType &one, dataType &two){	dataType  temp = one;	one = two;	two = temp;}