// irs.cpp#include <iostream.h>#include <iomanip.h>float singleTax (float);float marriedTax (float);main (){	char  status, choice;	float  income, tax;	do	{		cout << endl;		cout << "Enter filing status:  single (S/s) or Married (M/m) ---> ";		cin >> status;		cout << "Enter income ---> ";		cin >> income;		cout << setprecision (2) << setiosflags (ios::fixed | ios::showpoint);		if (status == 'S' || status == 's')		{			tax = singleTax (income);			cout << endl << "Single" << endl;		}		else if (status == 'M' || status == 'm')			{				tax = marriedTax (income);				cout << endl << "Married" << endl;			}		else cout << "Invalid filing status" << endl;		cout << "Taxable income = $" << income << endl;		cout << "Federal tax = $" << tax << endl << endl;		cout << "Do you wish to continue (y/n)? ";		cin >> choice;	}	while (choice == 'Y' || choice =='y');	return 0;}float singleTax (float income){	if (income <= 24650.0)		return (income * 0.15);	else		if (income <= 59750.0)			return (3697.50 + (0.28 * (income - 24650.0)));		else			return (13525.50 + (0.31 * (income - 59750.0)));}float marriedTax (float income){	if (income <= 41200.0)		return (income * 0.15);	else		if (income <= 99600.0)			return (6180.00 + (0.28 * (income - 41200.0)));		else			return (22532.0 + (0.31 * (income - 99600.0)));}