// mail.cpp#include <iostream.h>#include <iomanip.h>#include <bool.h>void dataInput (int&, int&, int&, int&);void printAnswer (int, int, int, int);void swap (int&, int&);main (){	int weight, dim1, dim2, dim3;	dataInput (weight, dim1, dim2, dim3);	printAnswer (weight, dim1, dim2, dim3);}void dataInput (int &wt, int &dim1, int &dim2, int &dim3)/*  Asks user for data: weight and 3 dimensions.  Will return weight	and the 3 dimensions in this order:  dim1 = length, dim2 and dim3	contain other 2 dimensions. */{	cout << "Enter the weight ---> ";	cin >> wt;	cout << "Enter 3 dimensions separated by spaces ---> ";	cin >> dim1 >> dim2 >> dim3;	if (dim2 > dim1)		swap (dim2, dim1);  // if dim2 is bigger, move into dim1	if (dim3 > dim1)		swap (dim3, dim1);  // if dim3 is bigger, move into dim1	cout << endl << endl;}void printAnswer (int wt, int length, int width, int height)//  prints out answers{	int  total = length + (width*2) + (height*2);	bool  tooLarge = (total > 100);	bool  tooHeavy = (wt > 70);	cout << "Weight = " << wt << " lbs;  ";	cout << "Length = " << length << "   Other two dimensions = ";	cout << setw(4) << width << setw(4) << height << endl;	cout << "     Package is ";	if (tooLarge && tooHeavy)		cout << "too large and too heavy" << endl;	else if (tooLarge && !tooHeavy)		cout << "too large" << endl;	else if (!tooLarge && tooHeavy)		cout << "too heavy" << endl;	else if (!tooLarge && !tooHeavy)		cout << "acceptable" << endl;	cout << endl;}void swap (int &a, int&b){	int temp = a;	a = b;	b = temp;}