#include <iostream.h>int  gcd (int, int);main (){	int  one, two;		do	{		cout << "Enter first integer ---> ";		cin >> one;		cout << "Enter second integer ---> ";		cin >> two;		if (one > 0 && two > 0)			cout << "gcd of " << one << " and " << two << " = " << gcd(one,two) << endl << endl;	}	while (one > 0 && two > 0);	return 0;}int gcd (int a, int b){	if (a < 1 || b < 1)		return 0;	else		if (a > b)		{			if (a % b == 0)				return b;			else				return gcd (b,a % b);		}		else		{			if (b % a == 0)				return a;			else				return gcd (a, b % a);		}}	