// loan.cpp#include <iostream.h>#include <iomanip.h>#include <math.h>void dataInput (float&, float&, float&, float&);void printAnswer (float, float, float, float);main (){	float  principal, low, high, years;	dataInput (principal, low, high, years);	printAnswer (principal, low, high, years);	return 0;}void printAnswer (float principal, float low, float high, float years){	double k,n,c,payment;	// for doing calculations	cout << setprecision (2) << setiosflags (ios::fixed | ios::showpoint | ios::right);	cout << "Mortgage problem" << endl << endl;	cout << "Time = " << years << endl;	cout << "Principal = $ " << principal << endl;	cout << "Low rate = " << low << " %" << endl;	cout << "High rate = " << high << " %" << endl << endl;	cout << setw(20) << "Annual Interest Rate";	cout << setw(20) << "Monthly Payment" << endl << endl;	n = years*12;	while (low <= high)	{		k = low/12/100;		c = pow (1+k, n);		payment = (principal * k * c) / (c - 1);		cout << setw(15) << low << " %";		cout << setw(10) << "$" << setw(9) << payment << endl;		low = low + 0.25;	}	cin.get();		// freezes the output screen}void dataInput (float &principal, float &low, float &high, float &years){	cout << "Data input for mortgage problem" << endl << endl;	cout << "Enter principal ---> ";	cin >> principal;	cout << "Enter low interest rate (%) ---> ";	cin >> low;	cout << "Enter high interest rate (%) ---> ";	cin >> high;	cout << "Enter number of years ---> ";	cin >> years;	cout << endl << endl;}