// payment.cpp#include <iostream.h>#include <iomanip.h>main (){	float  principal, interest, payment, total=0.0, rate, monthRate;	int    month=1;	cout << endl << "Enter principal ---> ";	cin >> principal;	cout << "Enter annual interest rate (%) ---> ";	cin >> rate;	cout << "Enter monthly payment ---> ";	cin >> payment;	monthRate = rate/12.0/100;	cout << setiosflags (ios::showpoint | ios::right | ios::fixed);	cout << setprecision(2);	cout << "Month      Principal      Interest      Payment    New Balance";	cout << endl << endl;	do	{		interest = principal * monthRate;		total += interest;		cout << setw(5) << month << setw(15) << principal;		cout << setw(14) << interest << setw(13) << payment;		principal = principal + interest - payment;		cout << setw(15) << principal << endl;		++month;	}	while (principal > payment);	cout << endl << setw(20) << "Total interest = ";	cout << setw(14) << total << endl;	return 0;}