// pictures.cpp#include <iostream.h>#include <iomanip.h>void printTable (int, int);void pyramid (int);main(){	printTable (4,6);	cout << endl << "Hit return key to continue ";	cin.get();	printTable (11,12);	cout << endl << "Hit return key to continue ";	cin.get();	pyramid (10);	cout << endl << "Hit return key to continue ";	cin.get();	pyramid (25);	cout << endl << "Hit return key to continue ";	cin.get();		return 0;}void printTable (int rownum, int colnum){	int  row, col;		cout << setiosflags (ios::right);	cout << endl << "      ";	for (col=1; col<=colnum; ++col)		cout << setw(4) << col;	cout << endl << endl;	for (row=1; row<=rownum; ++row)	{		cout << setw(3) << row << "   ";		for (col=1; col<=colnum; ++col)			cout << setw(4) << row*col;		cout << endl;	}}void pyramid (int n){	int line, blanks, stars, temp;	cout << endl;	for (line=1; line<=n; ++line)	{		blanks = n-line;		stars = line*2-1;		for (temp=1; temp<=blanks; ++temp)			cout << " ";		for (temp=1; temp<=stars; ++temp)			cout << "*";		cout << endl;	}}