// investment.cpp#include <iostream.h>#include <iomanip.h>main (){	float  principal, interest, extra, total=0.0, rate, monthRate;	int    month, number;	cout << endl << "Enter principal ---> ";	cin >> principal;	cout << "Enter annual interest rate (%) ---> ";	cin >> rate;	cout << "Enter monthly investment ---> ";	cin >> extra;	cout << "Enter number of months to run problem ---> ";	cin >> number;	monthRate = rate/12.0/100;	cout << setiosflags (ios::showpoint | ios::right | ios::fixed);	cout << setprecision(2);	cout << "Month      Principal      Interest      Investment    New Balance";	cout << endl << endl;	for (month=1; month<=number; month++)	{		interest = principal * monthRate;		total += interest;		cout << setw(5) << month << setw(15) << principal;		cout << setw(14) << interest << setw(13) << extra;		principal = principal + interest + extra;		cout << setw(18) << principal << endl;	}	cout << endl << setw(20) << "Total interest = ";	cout << setw(14) << total << endl;	return 0;}