// gpa.cpp// This program will figure out gpa and eligibility in activities.#include <iostream.h>#include <iomanip.h>#include <bool.h>void solve ();main (){	char choice;	cout << "GPA calculation program";	do {		solve ();		cout << endl << "Do you wish to compute another student's GPA (y/n)? ";		cin >> choice;	}	while (choice == 'Y' || choice == 'y' || choice == '\n');	cout << endl << endl;	return 0;}void solve (){	int  count=0, total=0;	bool  hasF = false;	char grade;	float gpa;	cout << endl << endl;	do	{		cout << "Enter grade, then return ---> ";		cin >> grade;		if ('A' <= grade && grade <= 'Z')			grade += 32;	// change to lowercase if necessary		if ('a' <= grade && grade <= 'd' || grade == 'f')		{			switch (grade)			{				case 'a' : total += 4;  break;				case 'b' : total += 3;  break;				case 'c' : total += 2;  break;				case 'd' : total += 1;  break;				case 'f' : hasF = true;  break;			}			++ count;		}	}	while ('a' <= grade && grade <= 'd' || grade == 'f');	gpa = float(total)/count;	cout << setprecision (2) << setiosflags (ios::fixed | ios::showpoint);	cout << endl << "GPA = " << gpa << "     ";	if (count < 4)		cout << "Inelgible, taking less than 4 classes" << endl;	else if (gpa < 2.0 && !hasF)		cout << "Inelgible, gpa below 2.0" << endl;	else if (gpa >= 2.0 && hasF)		cout << "Inelgible, gpa above 2.0 but has F grade" << endl;	else if (gpa < 2.0 && hasF)		cout << "Inelgible, gpa below 2.0 and has F grade" << endl;	else		cout << "Eligible" << endl;}