//  dice.h//  Borland Turbo C++ version#ifndef  DICE_H#define  DICE_H#include <stdlib.h>// random and randomize are inside of stdlib.hclass dice{	public:		dice(int sides);		// constructor		int roll();			// return the random roll		int numSides();		// returns how many sides on die		int numRolls();	// returns # times this die has been rolled	private:		int mySides;		int myRollCount;};dice::dice(int sides)// postcondition:  all private fields initialized// randomize() called to set random number generator{	randomize();	myRollCount = 0;	mySides = sides;}int dice::roll()// postcondition: # of rolls updated, returns random die roll{	myRollCount++;	return random(mySides)+1;}int dice::numSides()// postcondition:  return # of sides of die{	return mySides;}int dice::numRolls()// postcondition:  return # times die has been rolled{	return myRollCount;}#endif