//  testDice.cpp#include <iostream.h>#include <iomanip.h>#include <dice.h>main (){	dice die1(6), die2(6);	int number, loop, roll, count;	double percent;	cout << setiosflags (ios::fixed | ios::right) << setprecision (2);	for (number=2; number<=12; number++)	{		count=0;		for (loop=1; loop<=1000; loop++)		{			roll = die1.roll() + die2.roll();			if (roll == number)				count++;		}		percent = double(count)/1000*100;		cout << setw(2) << number << "   " << setw(3) << count;		cout << " / 1000" << setw(8) << percent << " %" << endl;	}	cout << endl;	cout << "Number of rolls of die1 = " << die1.numRolls() << endl;	cout << "Number of rolls of die2 = " << die2.numRolls() << endl;	return 0;}