// frog3.cpp#include <iostream.h>#include <frog.h>main(){	int numSteps, k, sameSpot = 0;	frog frog1, frog2;  		cout << "Enter number of steps for both frogs ---> ";	cin >> numSteps;	for (k=0; k<numSteps; k++)	{		frog1.takeStep();		frog2.takeStep();		if (frog1.getPosition() == frog2.getPosition())			sameSpot++;	}	cout << "position of frog1 = " << frog1.getPosition() << endl;	cout << "position of frog2 = " << frog2.getPosition() << endl;	cout << "number of overlaps = " << sameSpot << endl;	return 0;}