// rolling.cpp#include <dice.h>#include <iostream.h>main(){	dice die(6);	int a,b,c, count = 0;		do	{		a = die.roll();		b = die.roll();		c = die.roll();		cout << a << "  " << b << "  " << c << endl;		count++;	}	while ((a == b) || (a == c) || (b == c));	cout << "count = " << count << endl;	return 0;}/*Assertion when the loop is done:(a != b) && (a != c) && (b != c)Negation of the assertion:(a == b) || (a == c) || (b == c)*/