// daysApart.cpp#include "date.h"#include <iostream.h>int daysApart (date day1, date day2);void main (){	date day1, day2;	int  num, howMany;		cout << "How many dates do you want to compare? ";	cin >> howMany;	cout << endl;	for (int loop=1; loop<=howMany; loop++)	{		cout << "Enter day1 (mo day yr) ---> ";		cin >> day1;		cout << "Enter day2 (mo day yr) ---> ";		cin >> day2;		cout << endl;		num = daysApart (day1, day2);		cout << day1 << " and " << day2 << " are " << num << " days apart." << endl << endl;	}}int daysApart (date day1, date day2){	int num = 0;		if (day1 == day2)		return 0;	else if (day2 < day1)		return daysApart (day2, day1);  // one recursive call, switch places	else	{		while (day1 < day2)		{			day1++;			num++;		}		return num;	}}